tinymce.PluginManager.add("dlgmailto",function(e,t){function n(){e.windowManager.open({title:"Insert MailTo Link",body:[{type:"textbox",name:"mail",label:"E-mail address"}],onsubmit:function(t){e.execCommand("mceInsertContent",false,'<img alt="MailTo('+t.data.mail+')" name="MailTo" src="'+e.settings.plugin_mailto.plugin_img+'" title="MailTo" border="3" style="border-style:outset;">')}})}e.addButton("e_dlgmailto",{image:e.settings.plugin_mailto.img,tooltip:"Insert MailTo Link",onclick:n})})
